import React from 'react'
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import logo from '../assets/logo.svg';
import { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import "react-toastify/dist/ReactToastify.css";
import { ToastContainer, Toast } from 'react-toastify';

function Register() {

  const toastOptions = {
    position: "bottom-right",
    autoClose: 8000,
    pauseOnHover: true,
    draggable: true,
    theme: "dark",
  }
  const [values, setValues] = useState({
    username: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const handleSubmit = (event) => {
    event.preventDefault();
    handleValidation();
  }

  const handleValidation = () => {
    const {password, confirmPassword, username, email} = values;
    if (password !== confirmPassword) {
        toast.error("Passwords do not match", toastOptions);
        return false;
    } else if (username.length < 3){
        toast.error("Username must be at least 3 characters", toastOptions);
        return false;
    } else if (password.length < 8){
        toast.error("Password must be at least 8 characters", toastOptions);
        return false;
    } else if (email === ""){
        toast.error("Email is required", toastOptions);
        return false;
    }
    return true;
  }

  const handleChange = (event) => {
    setValues({...values, [event.target.name]: event.target.value});
  }

  return (
    <> 
        <FormContainer>
            <form onSubmit={handleSubmit}>
                <div className="brand">
                    <img src={logo} alt="logo" />
                    <h1>Pulsar</h1>
                </div>
                <input type="text" placeholder="Username" name="username" onChange={(event) => handleChange(event)}/>
                <input type="email" placeholder="Email" name="email" onChange={(event) => handleChange(event)}/>
                <input type="password" placeholder="Password" name="username" onChange={(event) => handleChange(event)}/>
                <input type="password" placeholder="Confirm Password" name="confirmPassword" onChange={(event) => handleChange(event)}/>
                <button type="submit">Create User</button>
                <span>Already have an account? <Link to="/login">Login</Link></span>
            </form>
        </FormContainer>
        <ToastContainer/>
    </>
  )
}

const FormContainer = styled.div`
    height: 100vh;
    width: 100vw;
    display: flex;
    flex-direction: column;
    justify-content: center;
    gap: 1rem;
    align-items: center;
    background-color: #131324;
    .brand {
        display: flex;
        align-items: center;
        gap: 1rem;
        justify-content: center;
        img {
            height: 5rem;
        }
        h1 {
            color: #fff;
            text-transform: uppercase;
        }
    }
    form {
        display: flex;
        flex-direction: column;
        gap: 2rem;
        background-color: #00000076;
        border-radius: 2rem;
        padding: 3rem 5rem;
        input {
            background-color: transparent;
            padding: 1rem;
            border: 0.1rem solid #4e0eff;
            border-radius: 0.4rem;
            color: white;
            width: 100%;
            font-size: 1rem;
            &:focus {
                border: 0.1rem solid #997af0;
                outline: none;
            }
        }
        button {
            background-color: #997af0;
            color: white;
            padding: 1rem 2rem;
            border: none;
            font-weight: bold;
            cursor: pointer;
            border-radius: 0.4rem;
            font-size: 1rem;
            text-transform: uppercase;
            transition: ease-in-out 0.5s;
            &:hover {
                background-color: #4e0eff;
            }
        }
        span {
            color: white;
            text-transform: uppercase;
            a {
                color: #4e0eff;
                text-decoration: none;
                font-weight: bold;
            }
        }
    }
`;

export default Register
