import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import loader from '../assets/loader.gif';
import { toast, ToastContainer } from 'react-toastify';
import "react-toastify/dist/ReactToastify.css";

import axios from 'axios';
import { registerRoute, setAvatarRoute } from '../utils/APIRoutes';

import { Buffer } from 'buffer';


function SetAvatar() {

  const api = `https://api.multiavatar.com/4645646`;
  const apiKey = '1s39KFwH7A8cqR';
  const navigate = useNavigate();

  const [avatars, setAvatars] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedAvatar, setSelectedAvatar] = useState(undefined);

  const toastOptions = {
    position: "bottom-right",
    autoClose: 8000,
    pauseOnHover: true,
    draggable: true,
    theme: "dark",
  };

  const setProfilePicture = async () => {

  }

  useEffect(() => {
    // Immediately invoked async function within useEffect
    (async () => {
      const data = [];
      try {
        for (let i = 0; i < 4; i++) {
          const image = await axios.get(`${api}/${Math.round(Math.random() * 1000)}`);
          const buffer = new Buffer(image.data);
          data.push(buffer.toString("base64"));
        }
        setAvatars(data);
      } catch (error) {
        console.error("Failed to fetch avatars:", error);
        // Optionally use toast to notify about the error
        toast.error("Failed to load avatars. Please try again later.", toastOptions);
      }
      setIsLoading(false);
    })();
  }, []);

  return (
    <>
        <Container>
            <div className='title-container'>
                <h1>Set your Avatar</h1>
            </div>
            <div className='avatars'>
                {avatars.map((avatar, index) => {
                    return (
                        <div key={index} className={`avatar ${selectedAvatar === index ? "selected" : ""}`}>
                            <img src={`data:image/svg+xml;base64,${avatar}`} 
                                 alt="avatar" 
                                 key={avatar}
                                 onClick={() => setSelectedAvatar(index)}/>
                        </div>
                    )
                })}
            </div>
            <ToastContainer/>
        </Container>
    </>
  )
}

const Container = styled.div`
    display: flex;
`

export default SetAvatar
